import time


def validate_spot(board, row, col):
    n = len(board)

    # test horizontal
    for j in range(n):
        if board[row][j] == 'Q' and j != col:
            return False

    # test vertical
    for i in range(n):
        if board[i][col] == 'Q' and i != row:
            return False

    # test diagonals
    d = 1
    while row - d >= 0 or col - d >= 0 or row + d < n or col + d < n:
        tl = (row - d, col - d)
        tr = (row - d, col + d)
        bl = (row + d, col - d)
        br = (row + d, col + d)
        for diag in [tl, tr, bl, br]:
            if 0 <= diag[0] < n and 0 <= diag[1] < n and board[diag[0]][diag[1]] == 'Q':
                return False
        d += 1
    return True

