def solve_n_queens(n):
    def backtrack(col, queens_remain):
        if queens_remain == 0:
            ans.append(["".join(row) for row in board])
            return

        for row in range(n):
            if validate_spot(board, row, col):
                board[row][col] = 'Q'
                backtrack(col + 1, queens_remain - 1)
                board[row][col] = '.'

    ans = []
    board = [["." for _ in range(n)] for _ in range(n)]
    backtrack(0, n)
    return ans


